frappe.ui.form.on('InterBank', {
    refresh(frm) {
        var current_day = frappe.datetime._date();  // Current date in YYYY-MM-DD format
        var current_time = frappe.datetime.now_time();  // Current time in HH:MM:SS format
        var type = frm.doc.type;
        var interbankDay = frm.doc.date;  // The date stored in the document
        var stopWatch = '11:56:00';  // End of the day time (you can adjust this as needed)

        // Check if the type is 'Daily' and the interbankDay matches the current day
        if (type == 'Daily' && current_day == interbankDay) {
            // Check if the current time has reached or passed the specified end time (stopWatch)
            if (current_time == stopWatch) {
                frappe.msgprint(`Hey King! The status is being updated.`); // Message for confirmation
                frm.set_value('status', 'Ended'); // Set the status to 'Ended'
                frm.save(); // Save the form after updating the status
            }
        }
    }
});

frappe.ui.form.on('InterBank Details', {
    currency_code(frm, cdt, cdn) {
        var d = locals[cdt][cdn];  
        var currency_code = d.currency_code; 
        var table = frm.doc.interbank;  
        var duplicated = false; 
        for (let row of table) {
            if (row.currency_code === currency_code && row.name !== d.name) {
                duplicated = true;
                break;
            }
        }

        if (duplicated) {
            frappe.msgprint(__(`Currency code  ${d.currency_code} appears more than one time`));
            d.currency_code = ''; 
        }
    }
});

